import React, { ReactNode } from "react";
import {createContext} from 'react'
import { ParkingContextState } from './types'
import {useState} from 'react'
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";


const contextDefaultValues : ParkingContextState = {
    parkingSlots: [],
    userInput: '',
    setUserInput: () => undefined,
    open: false,
    value : 0,
    setValue :() => undefined,
    setOpen : () => undefined,
    setForm : () => undefined,
    price: 0,
    time : 2,
    setTime : () => undefined,
    setPrice : () => undefined,
    form: {
        registration: "",
        time: ""
    },
    handleSubmit: () => {},
    handleSubmit2: () => {}

} 

interface Props {
    children?: ReactNode
}

const DataContext = createContext<ParkingContextState >(contextDefaultValues)


export const DataProvider = ({children} :Props) => {

    let navigate = useNavigate();
    const [userInput, setUserInput] = useState<string>("");
    const [form, setForm] = useState<{ registration: string; time: string }>({
      registration: "",
      time: "",
    });
    const [parkingSlots, setParkingSlots] = useState<any>([]);
    const [open , setOpen] = useState<boolean>(false)
    const [price , setPrice ] = useState<number>(20)
    const [time , setTime] = useState<number>(2)
     const [value , setValue] = useState<number>(0)


    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      let data = [];
      for (let i = 1; i <= Number(userInput); i++) {
        data.push({ id: i, isAlloted: false  });
      }
      navigate("/layout" );
      setParkingSlots(data);
      console.log(data);
    };


    const handleSubmit2 = (e: React.FormEvent) => {
      e.preventDefault();

      const nums = new Set();
      while (nums.size !== parkingSlots.length) {
       nums.add(Math.floor(Math.random() * parkingSlots.length) + 1);
      }
      const randomArray = [...nums]
      console.log(randomArray.pop() )
      let randomNum = parkingSlots[Math.floor(Math.random() * parkingSlots.length)];
      const slotBooked = parkingSlots.map((item: any) => {
      if (item === randomNum) {
        if (item.isAlloted !== true) {
          return {
            ...item,
            isAlloted: true,
            registration: form.registration,
            time: form.time,
          };
        } 
        else{
          const notify = () => toast("Please select another slot ");
          notify();
          return { ...item };
        }
      } else {
        return item;
      }
    });

    console.log(slotBooked)
    setParkingSlots(slotBooked);
    setForm({ registration: "", time: "" });
    };




     return(
         <DataContext.Provider value = {{
          userInput,  handleSubmit , parkingSlots, open  , price , form , handleSubmit2, setUserInput,
         setOpen, setForm, setPrice , time , setTime, value, setValue
         }}>
             {children}

         </DataContext.Provider>
     ) 
 }
 export default DataContext
